        taluka: {
          required: true,
          valueNotEquals: "-Select-"
        },
        companyState: {
          required: true,
          valueNotEquals: "-Select-"
        },
        companyCountry: {
          required: true,
          valueNotEquals: "-Select-"
        },
        companyPincode: {
          required: true,
          valueNotEquals: "-Select-"
        },
        companycontact: {
          required: true,
          regx3: /^(\+91[\-\s]?)?[0]?(91)?[789]\d{9}$/
        },
        companyaltcontact: {
          notEqual: $('#companycontact').val(),
          regx4: /^\d{5}([- ]*)\d{6}$|^(\+91[\-\s]?)?[0]?(91)?[789]\d{9}$|^$/
