import React, { Component }     from 'react';
import { render }               from 'react-dom';
import $ from "jquery";


class CompanyLocation extends Component{
  constructor(props) {
    super(props);
    this.state = {
      companyLocation       : "",
      Emailid               : "",
      companycontact        : "", 
      companyaltcontact     : "",
      companybuildingblock  : "",
      companylandmark       : "",
      companyCountry        : "",
      companyState          : "",

      companyDist           : "",
      taluka                : "",
      companyCity           : "",
      companyPincode        : "",
      submitVal            : true,

    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount(){
    $.validator.addMethod("regxA1", function(value, element, regexpr) {          
      return regexpr.test(value);
    }, "Name should only contain letters & space.");
    $.validator.addMethod("regxA3", function(value, element, regexpr) {          
      return regexpr.test(value);
    }, "Please enter a valid email.");
    $.validator.addMethod("regx3", function(value, element, regexpr) {          
        return regexpr.test(value);
    }, "Please enter a valid mobile number.");
    $.validator.addMethod("regxaddr", function(value, element, regexpr) {
      return regexpr.test(value);
    }, "Please enter alphanumeric only.");
    jQuery.validator.addMethod("notEqual", function(value, element, param) {
      return this.optional(element) || value != param;
    }, "Please specify a different value");
    $.validator.addMethod("valueNotEquals", function(value, element, arg){
          return arg !== value;
    }, "Value must not equal arg.");

    jQuery.validator.setDefaults({
      debug: true,
      success: "valid"
    });
    $("#companyLocationForm").validate({
      rules: {
        companyCity: {
          required:true,
          valueNotEquals: "-Select-"
        },
        companyDist: {
          required:true,
          valueNotEquals: "-Select-"
        },
        taluka: {
          required:true,
          valueNotEquals: "-Select-"
        },
        companyState: {
          required:true,
          valueNotEquals: "-Select-"
        },
        companyCountry: {
          required:true,
          valueNotEquals: "-Select-"
        },
        companyPincode: {
          required:true,
          valueNotEquals: "-Select-"
        },
        companycontact: {
          required: true,
          regx3: /^(\+91[\-\s]?)?[0]?(91)?[789]\d{9}$/,
        },
        companyaltcontact: {
          notEqual: $('#companycontact').val(),
          regx4: /^\d{5}([- ]*)\d{6}$|^(\+91[\-\s]?)?[0]?(91)?[789]\d{9}$|^$/,
        },
        companyEmailid: {
          required: true,
          regxA3: /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/, 
        },
        companyLocation: {
          regxA1: /^[a-zA-Z0-9\s, '/.-]+$/,
        },
        companyAddress:{
          required:true,
          regxaddr:/^[a-zA-Z0-9\s, '/.-]*$/
        }
      },

    }); 
    if (!$("#adminLte").length>0 && !$('body').hasClass('adminLte')) {
      var adminLte = document.createElement("script");
      adminLte.type = "text/javascript";
      adminLte.src = "/js/adminLte.js";
      adminLte.setAttribute('id','adminLte');
      $("body").append(adminLte);
    }
    $("html,body").scrollTop(0);

    //Spinner Effect
    $(document).ready(function() {
      $('.btn').on('click', function() {
        var $this = $(this);
        var loadingText = '<i class="fa fa-spinner fa-spin"></i> loading...';
        if ($(this).html() !== loadingText) {
          $this.data('original-text', $(this).html());
          $this.html(loadingText);
        }
        setTimeout(function() {
          $this.html($this.data('original-text'));
        }, 2000);
      });
    })
  }

  handleChange(event){
    const target = event.target;
    const name   = target.name;
    this.setState({
      [name]: event.target.value,
    });
  }
 submitCompanyLocation(event){
  event.preventDefault();
  var sessionVar = Session.get('location');

  var companyLocationFormValue ={
    companyLocation           : this.state.companyLocation,
    companyEmailid            : this.state.Emailid,
    companycontact            : this.state.companycontact,
    companyaltcontact         : this.state.companyaltcontact,
    companybuildingblock      : this.state.companybuildingblock,
    companylandmark           : this.state.companylandmark,
    companyCountry            : this.state.companyCountry,
    companyState              : this.state.companyState,

    companyDist               : this.state.companyDist,
    taluka                    : this.state.taluka,
    companyCity               : this.state.companyCity,
    companyPincode            : this.state.companyPincode,

  }//close array

  if($('#companyLocationForm').valid()){
    if(sessionVar){
      // $(".compLocationBtn").prop('disabled', false);
      Meteor.call('updateCompanyLocations', companyLocationFormValue,
        (error, result)=>{
          if(error){
            console.log(error);
          }else{
            if(result == 'DNF'){
              swal({
                title: 'Please fill company information first!',
                text: "Please fill company information first!",
                type: 'warning',
                showCancelButton: false,
                confirmButtonColor: '#666',
                // cancelButtonColor:'#d33',
                confirmButtonText: 'Ok'})
            }else{
              swal({
                title: 'Location details modified successfully!',
                text: "Location details modified successfully!",
                type: 'success',
                showCancelButton: false,
                confirmButtonColor: '#666',
                // cancelButtonColor:'#d33',
                confirmButtonText: 'Ok'})
                $('#companyLocation').prop('disabled', false);
                this.setState({
                  companyLocation      : '',
                  companyAddress       : '',
                  companybuildingblock      : '',
                  companyPincode       : '',
                  companyCity          : '',
                  companyState         : '',
                  companyCountry       : '',
                  companyMail          : '',
                  companycontact        : '',
                  companylandmark     : '',
                  taluka               : '',
                  companyDist          : '',
                  submitVal            : true,
                },()=>{
                  // if ($("#btnCheck").text("Update"))
                  // {     
                    // $("#btnCheck").html("Submit");
                  // }
                })
                Session.set('location',"");
                // if ($("#btnCheck").text("Update"))
                // {     
                //   $("#btnCheck").html("Submit");
                // }
            }

          }
        }
      );
    }else{

        var companyLocationFormValue ={

            companyLocation           : this.state.companyLocation,
            companyEmailid            : this.state.Emailid,
            companycontact            : this.state.companycontact,
            companyaltcontact         : this.state.companyaltcontact,
            companybuildingblock      : this.state.companybuildingblock,
            companylandmark           : this.state.companylandmark,
            companyCountry            : this.state.companyCountry,
            companyState              : this.state.companyState,

            companyDist               : this.state.companyDist,
            taluka                    : this.state.taluka,
            companyCity               : this.state.companyCity,
            companyPincode            : this.state.companyPincode,

      }//close array

      console.log("here insert loop enter", companyLocationFormValue);

        Meteor.call('insertCompanyLocations', companyLocationFormValue,(error, result)=>{
            if(error){
              console.log(error);
              swal('Oops...', 'Something went wrong!', 'error');
            }else{

              swal('Location Details Added Successfully!');


               // $("#companyLocationForm").validate().resetForm();

               this.setState({
                          companyLocation       : "",
                          Emailid               : "",
                          companycontact        : "", 
                          companyaltcontact     : "",
                          companybuildingblock  : "",
                          companylandmark       : "",
                          companyCountry        : "",
                          companyState          : "",

                          companyDist           : "",
                          taluka                : "",
                          companyCity           : "",
                          companyPincode        : "",
                      })


            }
          }
        );
      }
    }else{
      console.log("here outoff the conditions");
      $(event.target).parent().parent().find('input.error').addClass('companyError');
      $(event.target).parent().parent().find('select.error').addClass('companyError');
    }
  }
  companyListData(){
    var companyData = CompanySettings.findOne({"companyId" : 2});
    // console.log("companyData==================================>>>>>>>>>>>>>>",companyData);
    var companyarray = [];
    if(companyData){
      if(companyData.companyLocationsInfo){
        for(var i=0;i<companyData.companyLocationsInfo.length;i++){
          companyarray.push({
            'companyLocation'       : companyData.companyLocationsInfo[i].companyLocation,
            'Emailid'               : companyData.companyLocationsInfo[i].Emailid,
