editLocation=(event)=>{
  event.preventDefault();
  $("html,body").scrollTop(0);
  $("#companyLocationForm").validate().resetForm();
  var index = event.currentTarget.id;
  var companyData = CompanySettings.findOne({"companyId" : 2});
  if(companyData.companyLocationsInfo){
    this.setState({
      companyLocation      : companyData.companyLocationsInfo[index].companyLocation,
       companycontact        : companyData.companyLocationsInfo[index].companycontact,
      companybuildingblock : companyData.companyLocationsInfo[index].companybuildingblock,

      companyPincode       : companyData.companyLocationsInfo[index].companyPincode,
      companyCity          : companyData.companyLocationsInfo[index].companyCity,
      companyState         : companyData.companyLocationsInfo[index].companyState,
      companyCountry       : companyData.companyLocationsInfo[index].companyCountry,
      companyMail          : companyData.companyLocationsInfo[index].companyMail,

      companylandmark     : companyData.companyLocationsInfo[index].companylandmark,
      taluka               : companyData.companyLocationsInfo[index].taluka,
      companyDist          : companyData.companyLocationsInfo[index].companyDist,
      submitVal            : false, 
    })
  }
  // if ($("#btnCheck").text("Submit"))
  // {     
  //   $("#btnCheck").html("Update");
  // }
  // $('#companyLocation').prop('disabled', true);
  Session.set('location',companyData.companyLocationsInfo[index].companyLocation);
}

