import React,{Component} from 'react';
// import TrackerReact from 'meteor/ultimatejs:tracker-react';
import { render } from 'react-dom';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import $ from "jquery";
import Header from '../header/Header.js'


import './Leftsidebar.css';

export default class Leftsidebar extends Component{

  constructor(props) {
   super(props);
    this.state = {
        menuValues : {
            contactManagement     : false,
            dealManagement        : false,
            visitorsmanagement    : false,
            userManagement        : false,
            notification          : false,
            masterData            : false,
          }
    }
  }
  activeMenu(event){
    event.preventDefault();
    $(".sidebar-menu .treeview-menu a").removeClass("active-submenu");
    $(event.currentTarget).addClass("active-submenu");
  }
componentDidMount(){
                 /*$(document).ready(function () {
                 $('#sidebarCollapse').on('click', function () {
                     $('#sidebar').toggleClass('active');
                 });
             });*/
          }    

          openMenu = (key) => {
            let {menuValues} = this.state;
            Object.keys(menuValues).map((data) => {
              menuValues[data] = (data==key) ? !menuValues[key] :false;
            });
            this.setState({menuValues});
          }


  render(){
    let {contactManagement,dealManagement,visitorsmanagement,userManagement,notification,masterData} = this.state.menuValues;
    return(
    <div>
            <aside className="leftsidebar">
            <div className="wrapper">
            <nav id="sidebar">
                <div className="sidebar-header">
                    <h3>ProjectAdmin Sidebar</h3>
                    <strong>PS</strong>
                </div>

                <ul className="list-unstyled components">
                    <li className="active">
                    <a href="#">
                            <i className="glyphicon glyphicon-briefcase"></i>
                            Dashboard
                     </a>
                    </li>
                    <li>
                        <a href="#homeSubmenu" data-toggle="collapse" aria-expanded="false">
                            <i className="glyphicon glyphicon-home"></i>
                            Home
                        </a>
                        <ul className="collapse list-unstyled" id="homeSubmenu">
                            <li><a href="#">Home 1</a></li>
                            <li><a href="#">Home 2</a></li>
                            <li><a href="#">Home 3</a></li>
                        </ul>

                        <a href="#pageSubmenu" data-toggle="collapse" aria-expanded="false">
                            <i className="glyphicon glyphicon-duplicate"></i>
                            Pages
                        </a>
                        <ul className="collapse list-unstyled" id="pageSubmenu">
                            <li><a href="#">Page 1</a></li>
                            <li><a href="#">Page 2</a></li>
                            <li><a href="#">Page 3</a></li>
                        </ul>
                    </li>
                    <li className="treeview" data-name="contactManagement">
                        <a href="JavaScript:void(0);" data-toggle="collapse">
                            <i className="fa fa-address-book" aria-hidden="true"></i>
                            <span className="smsidenames sidebarMenuTitle">Contact Management</span>
                            <span className="pull-right-container">
                            <i className={"fa pull-right menu-icon-toggle "+(contactManagement?this.openIcon:this.closeIcon)} />
                            </span>
                        </a>
                        <ul className="treeview-menu collapse">
                            <li className="noPadLR">

                            <a href="/cm/add-new-contact" onClick={this.activeMenu.bind(this)}>
                            <i className="fa fa-circle-o dashr" />Add New Contact
                            </a> 
                            <a href="/cm/contact-details" onClick={this.activeMenu.bind(this)}>
                            <i className="fa fa-circle-o dashr" />Contact Details
                            </a> 
                            <a href="/cm/contact-email-history" onClick={this.activeMenu.bind(this)}>
                            <i className="fa fa-circle-o dashr" />Contact Email History
                            </a> 
                            <a href="/cm/contact-sms-history" onClick={this.activeMenu.bind(this)}>
                            <i className="fa fa-circle-o dashr" />Contact SMS History
                            </a> 

                            </li>

                        </ul>
                    </li>
                    <li>
                        <a href="#">
                            <i className="glyphicon glyphicon-link"></i>
                            Portfolio
                        </a>
                    </li>
                    <li>
                        <a href="#">
                            <i className="glyphicon glyphicon-paperclip"></i>
                            FAQ
                        </a>
                    </li>
                    <li>
                        <a href="#">
                            <i className="glyphicon glyphicon-send"></i>
                            Contact
                        </a>
                    </li>
                </ul>

               </nav>

            {/*<div id="content">

                <nav className="navbar navbar-default">
                    <div className="container-fluid">

                        <div className="navbar-header">
                            <button type="button" id="sidebarCollapse" className="btn btn-info navbar-btn">
                                <i className="glyphicon glyphicon-align-left"></i>
                            </button>
                        </div>
                   </div>
                   <h2></h2>
                <p></p>
                <p></p>

                </nav>
            </div>*/}
        </div>

            </aside>
      </div>
    );
  }
}
