import React,{Component} from 'react';
import TrackerReact from 'meteor/ultimatejs:tracker-react';
import { render } from 'react-dom';
import { FlowRouter }   from 'meteor/ostrio:flow-router-extra';
import {Notification} from '/imports/admin/notification/notifications/apiNotifications.js';
import AllNotifications   from '/imports/Common/headers/header2/AllNotifications.jsx';
import {withTracker} 		from 'meteor/react-meteor-data';


class Header2 extends TrackerReact(Component){
  constructor(){
    super();
     this.state={
      'remark':'',
      'id': '',
      'subscription':{
        'userNotification' : Meteor.subscribe('userNotification',Meteor.userId()),
        'currentUserfunction' : Meteor.subscribe('currentUserfunction'),
      }
     }
     this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount(){
    if ( !$('body').hasClass('adminLte')) {
      var adminLte = document.createElement("script");
      adminLte.type="text/javascript";
      adminLte.src = "/js/adminLte.js";
      $("body").append(adminLte);
    }
    $("html,body").scrollTop(0);
    }

    componentWillUnmount(){
      $("script[src='/js/adminLte.js']").remove();
      $("link[href='/css/dashboard.css']").remove();
    }

  logout(event){
    event.preventDefault();
    $("script[src='/js/adminLte.js']").remove();
    $("link[href='/css/dashboard.css']").remove();
    Meteor.logout();
    FlowRouter.go('/');
  }
  countadminNotification(event){
    var curUserId = Meteor.userId();
    if(curUserId){
      var notiCount = Notification.find({'toUserId':curUserId,'status':'unread'}).count();
      if(notiCount){
        return notiCount;     
      }else{
        return (0);
      }

    }
  }
  showAllNotification(){
    $('#showAllNotificationModal').modal('show');
    $('.modal-backdrop').hide();
  }

  showEmailNotification(){
    $('#showEmailNotificationModal').modal('show');
    $('.modal-backdrop').hide();
  }

  showReplyDiv(event){
    event.preventDefault();
    var id = event.currentTarget.id;
    var formId = 'replyRemarkModal-'+id;
    $('#'+formId).modal('show');
    $('.modal-backdrop').hide();
    this.setState({
      id:id
    })
  }

  handleChange(event){
    event.preventDefault();
    const target = event.target;
    const name   = target.name;
    this.setState({
      [name] : event.target.value,
    });
  }

  replyRemark(event){
    event.preventDefault();
    var toID = event.currentTarget.id;
    var fromID = event.currentTarget.name;
    var remark = this.state.remark;
    var getId = this.state.id;
    var formId = 'replyRemarkModal-'+getId;
    Meteor.call('insertUserNotification',fromID,'',toID,remark,(error,result)=>{
      if(error){
        console.log(error.reason);
      }else{
        this.setState({
          remark: ''
        })

        $('#'+formId).modal('hide');
        swal({
          title: 'abc',
          text : 'Notification sent!'
        })
        Meteor.call('updateNotification',getId,(error,result)=>{
          if(error){
            console.log(error.reason);
          }else{
            this.setState({
              id:''
            })
          }
        })

      }
    }); // end of insertNotification
  }

  getnotificationforadmin(){
    var userId       = Meteor.userId();
    var notifDetails = Notification.find({'toUserId':userId},{sort:{'date':-1}}).fetch();
    if(notifDetails){
      var notifArray = [];
      for(var i=0 ; i<notifDetails.length ; i++){
        var statusClass = '';
        if(notifDetails[i].status == "Read"){
        statusClass = 'statusColorRead';
        }

        if(notifDetails[i].notifBody){
          var notificationBody = notifDetails[i].notifBody;
        }else{
          var notificationBody = 'No new notification to show';
        }

        var notif            = notificationBody.slice(0,40);
        var notiDate         = notifDetails[i].date;
        var date             = moment(notiDate).fromNow()
        notifArray.push(
          <li key={i} className={statusClass} id={notifDetails[i]._id} >
          <a className={"notifContent "+statusClass} id={notifDetails[i]._id} onClick={this.showReplyDiv.bind(this)}>
             <span className="col-lg-12 col-md-12 col-sm-12 col-xs-12">{notif}</span>
             <span className="pull-right">{date}</span>
          </a>
          <div id={"replyRemarkModal-"+notifDetails[i]._id} className="modal fade" role="dialog">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h4 className="modal-title">Reply Remark</h4>
                  </div>
                  <div className="modal-body">
                    <div className="box-body"> 
                      <textarea rows="5" className="form-control errorinputText examDate col-lg-12 col-md-12 col-sm-12 col-xs-12" value={this.state.remark} onChange={this.handleChange} title="Please enter alphabets and numbers." id="remark" ref="remark" name="remark"/>
                    </div>
                  </div>
                  <div className="modDelFooter modal-footer">
                    <button type="button" className="btn btnClose" data-dismiss="modal">Close</button>
                    <button type="button" className={"btn updateBTNModal reply-"+notifDetails[i]._id} id={notifDetails[i].fromUserID} name={notifDetails[i].toUserId} data-index={notifDetails[i]._id} onClick={this.replyRemark.bind(this)}>Reply</button>
                  </div>
                </div>
              </div>
          </div>
        </li>
        );
      }//i
    }//notifDetails


    return notifArray;

  }
  userName(){
    var userData = Meteor.users.findOne({"_id":Meteor.userId()});
    if(userData){
      if(userData.profile){
        return userData.profile.firstname;
      }
    }

  } 

  emailId(){
    var userData = Meteor.users.findOne({"_id":Meteor.userId()});
    if(userData){
      return userData.emails[0].address;
    }
  }

	profileImage(){
		var imageData = this.props.post;
			if(imageData){
			if(imageData.profile && imageData.profile.userProfile){
			   return imageData.profile.userProfile;
			}else{
			   return '/images/userImage.png';
			}
			}else{
			return '/images/userImage.png';
		}
	}

  ProfileUpdate(event){
        var id = Meteor.userId();
        var userData = Meteor.user();
        var user = Meteor.users.findOne({'_id':id});
        var department = userData.profile.department;

        var userRole = user.roles[0];
        // console.log('userRole ==>',userRole);
        if (userRole == 'superAdmin' || userRole == 'Admin' || userRole == 'admin' ){
          FlowRouter.go('/Profile/'+id);
        }


  }
  companysetting(){
      FlowRouter.go('/companyinfo');
  }

  render(){
    return(
    <div className="">
      <header className="main-header newMain-header">

        <a href="javascript:void(0)" className="logo">
          <span className="logo-mini"><img src="/images/logo-temp.png" className="logocr"/></span>
          <span className="logo-lg">
              {/*<img src="/images/companyLogo.png" className="dashboardLogoOES" />*/}
              <h3>SRES</h3>
          </span>
        </a>

        <nav className="navbar navbar-static-top" role="navigation">
           <a href="javascript:void(0)" className="sidebar-toggle marginTop11 marginLeft12" data-toggle="push-menu" role="button">
              <div className="barClass">
                <div className="one"></div>
                <div className="two"></div>
                <div className="three"></div>
              </div>
              <span className="sr-only">
                Toggle navigation
              </span>
            </a>
          <div className="navbar-custom-menu">
            <ul className="nav navbar-nav">
              <li className="dropdown messages-menu">
                  <a href="#" className="dropdown-toggle pdli" data-toggle="dropdown">
                    <i className="fa fa-bell-o"></i>
                    <span className="label label-danger">{this.countadminNotification()}</span>
                  </a>
                  <ul className="dropdown-menu">
                     <li className="header">You have {this.countadminNotification()} notifications</li>
                      <li>
                        <ul className="menu">
                          {this.getnotificationforadmin()}

                        </ul>
                      </li>
                    <li className="footer"><a onClick={this.showEmailNotification.bind(this)}>See All Messages</a></li>
                  </ul>
                </li>
                <div id="showEmailNotificationModal" className="modal fade" role="dialog">
                  <div className="modal-dialog">
                    <div className="modal-content">
                      <div className="modal-header">
                      <button type="button" className="close" data-dismiss="modal">&times;</button>
                        <h4 className="modal-title">View All Notification</h4>
                      </div>
                      <div className="modal-body">
                        <div className="box-body"> 
                          <AllNotifications />
                        </div>
                      </div>
                      <div className="modDelFooter modal-footer">
                        <button type="button" className="btn btnClose" data-dismiss="modal">Close</button>
                      </div>
                    </div>
                  </div>
              </div>

              <div id="showAllNotificationModal" className="modal fade" role="dialog">
                  <div className="modal-dialog">
                    <div className="modal-content">
                      <div className="modal-header">
                      <button type="button" className="close" data-dismiss="modal">&times;</button>
                        <h4 className="modal-title">View Email Notification</h4>
                      </div>
                      <div className="modal-body">
                        <div className="box-body"> 
                          <AllNotifications />
                        </div>
                      </div>
                      <div className="modDelFooter modal-footer">
                        <button type="button" className="btn btnClose" data-dismiss="modal">Close</button>
                      </div>
                    </div>
                  </div>
              </div>

              <li className="dropdown messages-menu user user-menu">
                <a href="#" className="dropdown-toggle pdli" data-toggle="dropdown">
                  <img src={this.profileImage()?this.profileImage() :"/images/preloader.gif"} className="user-image img-circle" alt="User Image"/>
                  {/* {
                  <img className=" ClientImgWrapmainheader123  img-circle" 
                  src={this.profileImage()?this.profileImage() :"/images/preloader.gif"}/>
                  } */}
                          <span className="headerusername">
                            {this.userName()}
                          </span>

                  <span className="hidden-xs angleright"><i className="fa fa-angle-down" aria-hidden="true"></i></span>
                </a>

                <ul className="dropdown-menu dropmenu">
                <div className="arrow-up"></div>
                  <li className="user-header">
                    <ul className="menu">
                      <li>
                        <a >
                          {/* <div className="pull-left imgcss">
                            <img src="/images/dummyImg.png" className="img-circle" alt="User Image"/>

                            {   
                              this.profileImage() ==='/images/userImage.png' 
                            }

                          </div> */}
                          {<img className="col-lg-12 col-md-12 col-sm-12 ClientImgWrap123 displayLogoOneheader img-circle" src={this.profileImage()?this.profileImage() :"/images/preloader.gif"}/>}
                          <h4>
                            {this.userName()}
                          </h4>
                          <p className="why">{this.emailId()}</p>
                         <div className="" onClick={this.companysetting.bind(this)}>
                            <i className="fa fa-dribbble cmpysethdr fonticonshdr"></i>
                            <span className="compannysethdr"> Company Setting</span>
                          </div>

                        </a>
                        <div className="profilefoot"> 
                        <div>
                          <span className="pull-left">

                            <a  className="logout profileTitle" onClick={this.ProfileUpdate.bind(this)}> <button type="button" className="profilebtn">Profile</button></a>
                          </span>
                          <span className="pull-right">
                            <a href="javascript:void(0)" className="logout" onClick={this.logout.bind(this)}> <button type="button" className="profilebtn">Logout</button></a>
                         </span>  
                        </div>
                        </div>
                      </li>
                    </ul>
                  </li>
                  <div className="bordred">
                </div>             
                </ul>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      </div>
    );
  }
}
