import React,{Component} from 'react';
// import TrackerReact from 'meteor/ultimatejs:tracker-react';
import { render } from 'react-dom';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import $ from "jquery";
import Header from '../common/header/Header.js'
import Footer from '../common/footer/Footer.js'
import Dashboard from '../dashboard/Dashboard.js'
import Leftsidebar from '../common/leftSidebar/Leftsidebar.js'
import Rightsidebar from '../common/rightSidebar/Rightsidebar.js';



 class Layout extends Component{

  constructor(props) {
   super(props);
    this.state = {}
  }

componentDidMount(){
                 $(document).ready(function () {
                 $('#sidebarCollapse').on('click', function () {
                     $('#sidebar').toggleClass('active');
                 });
             });

                 $(document).ready(function () {
                 $('#sidebarCollapse').on('click', function () {
                     $('#headerid').toggleClass('headereffect');
                 });
             });
                 $(document).ready(function () {
                 $('#sidebarCollapse').on('click', function () {
                     $('#dashbordid').toggleClass('dashboardeffect');
                 });
             });

  }

  render(){
    return(

            <div className="App container-fluid">
                <div className="row">
                  <div id="headerid" className="headerbackgroundcolor ">
                    <div className="">
                      <Header />
                   </div>
                  </div>
                  <div className="dasbordbackgroundcolor col-lg-10 col-md-10 col-sm-10 col-xs-10 col-lg-offset-2 col-md-offset-2 col-sm-offset-2 col-xs-offset-2">
                    <div id="dashbordid" className="">
                  {content}
                    </div>
                  </div>
                  <div className="leftsidebarbackgroundcolor">
                    <div className="row">
                       <Leftsidebar />
                    </div>
                  </div>
                  <div className="col-lg-10 col-md-10 col-sm-10 col-xs-10 col-lg-offset-2 col-md-offset-2 col-sm-offset-2 col-xs-offset-2">
                    <div className="">

                   </div>
                  </div>
                </div>
            </div>

        );
  }
}
export default Layout;



