import React, {Component}           from 'react';
import {render}                     from 'react-dom';
import $ from "jquery";
import CompanyInformation           from  './CompanyInformation.js';
import CompanyLocation              from  './CompanyLocation.js';
import '../css/CompanySetting.css';
// import CompanyBankDetails           from  '/imports/admin/companySetting/Company_bank_details/CompanyBankDetails.jsx';
// import CompanyTaxDetails            from  '/imports/admin/companySetting/Company_bank_details/CompanyTaxDetails.jsx';
// import AddContactCategory           from  '/imports/admin/companySetting/Add_Contact_Category/AddContactCategory.jsx';
// import AddPropertyType           from  '/imports/admin/companySetting/Add_Property_subproperty/AddPropertyType.jsx';

 class CompanySetting extends Component{
  componentDidMount() {


  }


  componentWillUnmount(){

  }
  clickLi(event){
    event.preventDefault();
    // $("#companyLocationForm").validate().resetForm();
    // $("#companyInformationForm").validate().resetForm();
    // $('.error').css({'display':'none'})
  }

  companyinformationbtn(event) {
    // event.preventDefault();
    $('#companyinformationbtn').css({ 'display': 'none' });
    $('#companylocation').css({ 'display': 'block' });


    this.setState({
        notification: "Property",
    });

}
companylocation(event) {
    // event.preventDefault();
    $('#notificationmap').css({ 'display': 'none' });
    $('#companylocation').css({ 'display': 'block' });
    $('#notifiactionmeeting').css({ 'display': 'none' });
    $('#notifiactionnotes').css({ 'display': 'none' });
    $('#notifiactionagreement').css({ 'display': 'none' });
    $('#notifiactiontransaction').css({ 'display': 'none' });

    this.setState({
        notification: "Meeting",
    });
}
notifiactionnotesbtn(event) {
    $('#notificationmap').css({ 'display': 'none' });
    $('#notifiactionproperty').css({ 'display': 'none' });
    $('#notifiactionmeeting').css({ 'display': 'block' });
    $('#notifiactionnotes').css({ 'display': 'none' });
    $('#notifiactionagreement').css({ 'display': 'none' });
    $('#notifiactiontransaction').css({ 'display': 'none' });

    this.setState({
        notification: "Notes",
    });
}
notifiactionagreementbtn(event) {
    $('#notificationmap').css({ 'display': 'none' });
    $('#notifiactionproperty').css({ 'display': 'none' });
    $('#notifiactionmeeting').css({ 'display': 'none' });
    $('#notifiactionnotes').css({ 'display': 'block' });
    $('#notifiactionagreement').css({ 'display': 'none' });
    $('#notifiactiontransaction').css({ 'display': 'none' });

    this.setState({
        notification: "Agreement",
    });
}
notifiactiontransactionbtn(event) {
    $('#notificationmap').css({ 'display': 'none' });
    $('#notifiactionproperty').css({ 'display': 'none' });
    $('#notifiactionmeeting').css({ 'display': 'none' });
    $('#notifiactionnotes').css({ 'display': 'none' });
    $('#notifiactionagreement').css({ 'display': 'block' });
    $('#notifiactiontransaction').css({ 'display': 'none' });

    this.setState({
        notification: "Transaction",
    });
}
  render() {

    return (
       <div>
          <div className="content-wrapper">
             <section className="content viewContent">
                <div className="row">
                   <div className="col-lg-12 col-md-12 col-xs-12 col-sm-12">
                      <div className="box col-lg-12 col-md-12 col-xs-12 col-sm-12">
                         <div className="boxMinHeight boxMinHeighttab">
                         <div className="box-header with-border">

                               <h4 className="MasterBudgetTitle">Company Settings</h4>

                          </div>
                          {/* <div className="col-lg-12 col-md-12 col-xs-12 col-sm-12 titleaddcontact"> Company Settings </div>         */}

                            <div  className="col-lg-12 col-md-12 col-xs-12 col-sm-12 companySettingMarginTop NOpadding-left">
                              <div className="col-lg-3 col-md-3 col-xs-12 col-sm-12 noPadding"> 
                                <ul className="nav nav-tabs tabs-left sideways">
                                  <li className="active  col-lg-12 col-md-12 col-xs-12 col-sm-12" onClick={this.companyinformationbtn.bind(this)}><a className="tabLeft tablefthr lettersp" href="#companyInformation" data-toggle="tab">Company Information</a></li>
                                  <li className="col-lg-12 col-md-12 col-xs-12 col-sm-12" onClick={this.companylocation.bind(this)}><a className="tabLeft lettersp tablefthr" href="#CompanyLocation" data-toggle="tab">Location Details</a></li>
                                   <li className="col-lg-12 col-md-12 col-xs-12 col-sm-12" onClick={this.clickLi.bind(this)}><a className="tabLeft lettersp tablefthr" href="#CompanyBankDetails" data-toggle="tab">Bank Details</a></li>

                                   <li className="col-lg-12 col-md-12 col-xs-12 col-sm-12" onClick={this.clickLi.bind(this)}><a className="tabLeft lettersp tablefthr" href="#AddContactCategory" data-toggle="tab">Contact Group</a></li>
                                   <li className="col-lg-12 col-md-12 col-xs-12 col-sm-12" onClick={this.clickLi.bind(this)}><a className="tabLeft lettersp tablefthr" href="#AddPropertyType" data-toggle="tab">Property Type</a></li>

                                </ul>
                              </div>

                              <div className="tab-content col-lg-9 col-md-9 col-xs-12 col-sm-12">
                                <div className="tab-pane active" id="companyInformation"> <CompanyInformation/> </div>
                                <div className="tab-pane" id="CompanyLocation"> <CompanyLocation/> </div>
                                {/* <div className="tab-pane" id="CompanyBankDetails"> <CompanyBankDetails/> </div>

                                <div className="tab-pane" id="AddContactCategory"> <AddContactCategory/> </div>
                                <div className="tab-pane" id="AddPropertyType"> <AddPropertyType/> </div> */}

                              </div> 
                              <div className="clearfix"></div>
                            </div>
                         </div>
                      </div>
                   </div>
                </div>
             </section>
          </div>
      </div>
    );

  }

}
export default CompanySetting;
