    //     if(res[0]=="admin"){
    //       this.setState({
    //         loginuserRole : res[1]
    //       },()=>{this.getadminLTE()});
    //     }
    //   }
    // });
    this.getadminLTE();
  }

  getadminLTE(){
      if ($('body').hasClass('adminLte')) {
        var adminLte = document.createElement("script");
        adminLte.type="text/javascript";
        adminLte.src = "/js/adminLte.js";
        $("body").append(adminLte);
      }
  }

  componentWillUnmount(){
    $("script[src='/js/adminLte.js']").remove();
    $("link[href='/css/dashboard.css']").remove();
  }

  removePersistantSessions(){
      // UserSession.delete("progressbarSession", Meteor.userId());
      // UserSession.delete("allProgressbarSession", Meteor.userId());
  }

  currentUser(){
    // var LoginUserData = Meteor.users.findOne({"_id":Meteor.userId()});
    // if(LoginUserData){
    //   var profile = LoginUserData.profile;
    //   if(profile){
    //     var firstName = profile.firstname;
    //   }
    //   return firstName;
    // }

  }

  render(){

    return(

        <aside className="main-sidebar" onClick={this.removePersistantSessions.bind(this)}>
          <section className="sidebar">


            <ul className="sidebar-menu" data-widget="tree">
              <li className="">
                <Link className="reactRouterlist bgclr" to="/adminDashboard" activeClassName="active">
                  <i className="fa fa-dashboard" />
                    <span className="bgclr">Dashboard</span>
                </Link>
              </li>



               <li className="treeview bgclr">
                  <Link className="reactRouterlist bgclr" to="javascript:void(0)">
                    <i className="fa fa-university" aria-hidden="true"/>
                    <span>Company Settings</span>
                    <span className="pull-right-container">
                      <i className="fa fa-angle-left pull-right" />
                    </span>
                  </Link>
                 <ul className="treeview-menu">
                   {/* <li>
                      <a href="/franchise/FranchiseDetails">
                        <i className="fa fa-university" aria-hidden="true"/> Add Franchise Details
                      </a>
                    </li>*/}
                    <li>
                      <Link className="reactRouterlist bgclr" to="/admin/companyinfo">
                        <i className="fa fa-graduation-cap" aria-hidden="true"/> Company Details
                      </Link>
                    </li>
                  </ul>
                </li>

                 <li className="treeview bgclr">
                  <Link className="reactRouterlist bgclr" to="javascript:void(0)">
                    <i className="fa fa-bell" aria-hidden="true"/>
                    <span>Notification</span>
                    <span className="pull-right-container">
                      <i className="fa fa-angle-left pull-right" />
                    </span>
                  </Link>

                </li>
                <li className="treeview bgclr">
                  <Link className="reactRouterlist bgclr" to="javascript:void(0)">
                    <i className="fa fa-comments" aria-hidden="true"/>
                    <span>SMS Management</span>
                    <span className="pull-right-container">
                      <i className="fa fa-angle-left pull-right" />
                    </span>
                  </Link>
                 <ul className="treeview-menu">



                  </ul>
                </li>




               <li className="treeview bgclr">
                <Link className="reactRouterlist bgclr" to="javascript:void(0)">
                  <i className="fa fa-book" />
                  <span> Competition Master </span>
                  <span className="pull-right-container">
                    <i className="fa fa-angle-left pull-right" />
                  </span>
                </Link>
                <ul className="treeview-menu">
                  <li className="">
                    <Link className="reactRouterlist bgclr" to="/Admin/CreateExams">
                      <i className="fa fa-circle-o" /> Create Competition
                    </Link>
                  </li>
                  <li className="">
                    <Link className="reactRouterlist bgclr" to="/Admin/ListofExams">
                      <i className="fa fa-circle-o" /> Manage Competitions
                    </Link>
                  </li>

                </ul>
              </li>             
              {/*<li className="treeview">
