													return(
															<span key="index">{data.bedrooms}&nbsp; BHK Flat on Rent</span>
														);
													})
												:
												"2 BHK"
												}</div>
											</span>
										</div>
										<div className="col-lg-5">
											<span className="mt-8">
												{property.ownerDetails.userName? property.ownerDetails.userName: "Rushikesh " }<br/>
												{property.ownerDetails.emailId ? property.ownerDetails.emailId : "rushikesh.salunkhe101@gmail.com"}<br/>
												{property.ownerDetails.mobileNumber ? property.ownerDetails.mobileNumber : "*** **** *** "}

											</span>
										</div>
										<div className="col-lg-3 pull-right fSize13">
											{
												this.props.status ==="WIP" ?
												<div>
													{moment(property.createdAt).format('MMMM Do YYYY')} &nbsp;
													{moment(property.createdAt).format('LT')} &nbsp;
												</div>
												:
												<div>
													{moment(property.propertyCreatedAt).format('MMMM Do YYYY')} &nbsp;
													{moment(property.propertyCreatedAt).format('LT')} &nbsp;
												</div>
											}
											<div id="myProgress">
												<Progressbar data="80" />
											</div>
										</div>
										<img src="/images/cancel.png" className="cancelImg"  id={property._id} onClick={this.handleDelete.bind(this)}/>
									</div>
								</div>
							);
							})

					:

					<div>
						<h5>Under construction</h5>
					</div>

				}
			{/*modal=============*/}
				 <div className="modal fade" id="myModal" role="dialog">
				    <div className="modal-dialog">
				      <div className="modal-content">
				        <div className="modal-header">
				          <button type="button" className="close" data-dismiss="modal">&times;</button>
				          <h4 className="modal-title">Meeting </h4>
				        </div>
				        <div className="modal-body">
				          <p>Some text in the modal.</p>
				          <input type="date" />
				          <input type="time" />
				          <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 margBtm_30 dateIndex">
              <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12">
                <div className="form-group margBtm_5" id="date">
                  <div className="input-group inputBox-main " id="">
                    <div className="input-group-addon inputIcon">
                        <i className="fa fa-building iconClr"></i>
                    </div>
                    {/*<input type="date" className="form-control" name="availableFrom" ref="availableFrom" value={this.state.availableFrom} onChange={this.handleChange.bind(this)} id="" min="1900-01-01" max="2100-12-31"/>*/}
                   <DatePicker
                      selected={this.state.startDate}
                      onChange={this.handleDate}
                      dateFormat="dd-MM-yyyy"
                      className="form-control col-lg-12 "
                      minDate={(new Date())}
                      name="availableFrom"
                      ref="availableFrom"
                      // value={this.state.availableFrom}
                      max="2100-12-31"
                      onKeyDown={e=>e.preventDefault()}

                    />
                  </div>
                </div>
            </div>
            <div className="col-lg-6 col-md-6 col-sm-6 col-xs-6"  id="" >
					    <div className="input-group">

							  <TimePicker
							    showSecond={false}
							    className="xxx"
							    value={this.state.now}
							    onChange={this.fromTime.bind(this)}
							    format={format}
							    use12Hours
							    inputReadOnly
							    className="timePicHeight"

							  />

					  	</div>
					</div>
          </div>
				        </div>
				        <div className="modal-footer">
				          <button type="button" className="btn btn-default" data-dismiss="modal">Close</button>
				        </div>
				      </div>
				    </div>
				  </div>
				{/*modal end==============*/}
			</div>
		)
	}
}
export default withRouter(Properties);
