                                    <b>
                                        <li className="col-lg-5 col-xs-6 noPad">Monthly Rent    </li> 
                                        <span className="col-lg-7 col-xs-6 noPad"> : <b><i className="fa fa-inr pr8" aria-hidden="true"></i>{this.state.pricing.monthlyRent}</b></span>
