                                          var comma = ", ";
                                          var i = index;
                                          if(index >= (this.state.pricing.includeCharges.length-1) ){
                                            comma = "";
                                          }
                                          return(
                                              <b key={i++}>
                                                  {" "+includeCharges+comma}
                                              </b>                  
                                            )
                                          })
                                    }  </b> : "-"}
                                    </span>
                                    </div>
                                    }
