                          <button className="mapButton btn btn-primary" onClick={this.handleMap.bind(this)}>Show Map</button>
                        </div>
                      }
                   </div>
                    </div>              
                    </div>
                  </div>
                </div>
               </div>
            </div>              
          </div>
        </div>
