const mapStateToProps = state => {
  return {
    uid: state.uid,
    property_id: state.property_id,
    BasicInfo: state.BasicInfo,
    PropertyDetails: state.PropertyDetails,
    Financials: state.Financials,
    Amenities: state.Amenities,
    Availability: state.Availability,
    Location: state.Location,
    // prop_id         : state.prop_id,
    updateStatus: state.updateStatus
  };
};

const mapDispatchToProps = dispatch => {
  return {
    redirectToPropertyDetails: (uid, property_id) => dispatch({
      type: "REDIRECT_TO_PROPERTY",
      uid: uid,
      property_id: property_id
    }),
    backToBasicInfo: (uid, property_id) => dispatch({
      type: "BACK_TO_BASIC_INFO",
      uid: uid,
      property_id: property_id
    })
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Location));

