import React , { Component }	from 'react';
import axios 					from 'axios';
import NavTab 					from '../NavTab/NavTab.js';
import $ 						from "jquery";
import {withRouter} from 'react-router-dom';
import 'bootstrap/js/tab.js';
import "bootstrap/dist/css/bootstrap.min.css";
import 'bootstrap/js/modal.js';
import LoginMobNum from '../LoginMobNum/LoginMobNum.js';
import LoginOtp from '../LoginOtp/LoginOtp.js';
import WebSignupForm from '../WebSignup/WebSignupForm.js';

import { connect } 				from 'react-redux';


import Form1 from '../Form1/Form1.js';

import Form2 from '../Form2/Form2.js';

import Form3 from '../Form3/Form3.js';

import Form4 from '../Form4/Form4.js';

import Form5 from '../Form5/Form5.js';

import Location from '../Location/Location.js';




import './Banner.css';


class BannerwithModal extends Component {

	constructor(){
		super();
		this.state = {
			// LoginMobNum 	: true,
			// LoginOtp 		: false,
			// WebSignupForm 	: localStorage.getItem("WebSignupForm"),
		}
	}

	removeBackdrop(){
 		$(".modal-backdrop").remove();		
	}


	render() {
		return (
			<div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 mb90">
				<div className=" col-lg-12  ">
					<h2 className="hText">EVERY DREAM HAS A KEY</h2>
					<h4 className="hText1">India's Only Property Portal sharing Brokerage with both Owners and Tenants!</h4>
				</div>
				<div className="col-lg-6 col-lg-offset-4 mt-10">
					<button id="Buy" 		className="col-lg-3 btn btn-bg" >Buy</button>
					<button id="Rent" 		className="col-lg-3 btn btn-bg ml-10">Rent</button>
					<button id="Commercial" className="col-lg-3 btn btn-bg ml-10">Commercial</button>
				</div>
				<div className="col-lg-5 col-lg-offset-3">
					<div className="col-lg-10">
						<input type="text" placeholder="  Enter Society, Location or Address" className="hSearch"  />
					</div>
					<div className="col-lg-1 S-img">
						<div className=" col-lg-12 S-img1 ">
							<img src="/images/TGK-key.png" />
						</div>
					</div>
				</div>
				<div className="col-lg-6 col-lg-offset-4 mt-64">
					<div className="col-lg-5 br2">
						<span className="postDetails">Welcome Owners</span>
						<button className="btn bg-primary btnPost" data-toggle="modal" data-target="#postPropertyModal"> Post & Earn</button>
						<br/>
						<span><b> Earn upto 50% Brokerage for <br/>Listing With Us!</b>
						</span>
					</div>
					<div className="col-lg-5 prText">
						<span className="postDetails ">Tenants/Buyers</span><br/>
						<p className="postDetails1"><b>Upto 50% Discount</b>
						<br/><b>On Brokerage</b>
						<br/><b>for Renting/Buying with us!</b>
						</p>

					</div>
				</div>


			{/*=== Modal starts here ===*/}
				<div id="postPropertyModal" class="modal fade" role="dialog">
				  <div class="modal-dialog modal-lg">

				    <div class="modal-content">
				      <div class="modal-header">
						<h1> {this.state.LoginMobNum} </h1>
						<h1> {this.state.LoginOtp} </h1>
						{/*<h1> {this.state.WebSignupForm} </h1>*/}

				        <button type="button" class="close" data-dismiss="modal" onClick={this.removeBackdrop.bind(this)}>&times;</button>
				        <h4 class="modal-title">
				        	<b> Owners earn upto 50% brokerage by selling/renting with us so let's get started </b>
				        </h4>
				      </div>

				      <div class="modal-body col-lg-12">
				      	<h1> {this.props.LoginMobNum} </h1>				        
				      	<h1> {this.props.LoginOtp} </h1>				        
				      	<h1> {this.props.WebSignupForm} </h1>				        

						{ this.props.LoginMobNum 	? <LoginMobNum /> 	: null }
						{ this.props.LoginOtp 		? <LoginOtp /> 		: null }
						{ this.props.WebSignupForm 	? <WebSignupForm /> : null }

				      </div>

				      <div class="modal-footer">
				        {/*<button type="button" class="btn btn-primary" data-dismiss="modal">Next</button>*/}
				      </div>

				    </div>

				  </div>
				</div>







					<div id="LoginMobNum" className="modal ssmodal">
                    <span className="loginmodalclose" >X</span>
                        <LoginMobNum />
                    </div>

                    <div id="LoginOtp" className="modal ssmodal">
                    <span className="signupmodalclose">X</span>
                        <LoginOtp />
                    </div>

                    <div id="WebSignupForm" className="modal ssmodal">
                    <span className="forgetpassmodalclose">X</span>
                        <WebSignupForm />
                    </div>

                    <div id="Form1" className="modal ssmodal">
                    <span className="resetpassmodalclose">X</span>
                    	<Form1 />
                    </div>
					<div id="Form2" className="modal ssmodal">
                    <span className="resetpassmodalclose">X</span>
                    	<Form2 />
                    </div>
					<div id="Form3" className="modal ssmodal">
                    <span className="resetpassmodalclose">X</span>
                    	<Form3 />
                    </div>
					<div id="Form4" className="modal ssmodal">
                    <span className="resetpassmodalclose">X</span>
                    	<Form4 />
                    </div>
					<div id="Form5" className="modal ssmodal">
                    <span className="resetpassmodalclose">X</span>
                    	<Form5 />
                    </div>
					<div id="Location" className="modal ssmodal">
                    <span className="resetpassmodalclose">X</span>
                    	<Location />
                    </div>


			</div>
		);
	}
}

const mapStateToProps = (state)=>{
	return {
		LoginMobNum 	: state.LoginMobNum,
		LoginOtp 		: state.LoginOtp,
		WebSignupForm 	: state.WebSignupForm,
	}
};
const mapDispatchToProps = (dispatch)=>{
	return {
	}
};


export default connect(mapStateToProps, mapDispatchToProps)(withRouter(BannerwithModal));
